import React from 'react'
import {ProductConsumer} from '../../context'
import CartItems from './CartItem'

export default function CartList() {
    return (
    <div className="container-fluid">
    <div className="row">
    <div className="col">
    <ProductConsumer>
        {value => {
            const {cart, increment, decrement, removeItems, clearCart} = value;

            if(cart.lenght === 0){
                return (
                    <h1 className="text-title text-center my-4">
                    your cart is empty
                    </h1>
                )
            }

        }}
    </ProductConsumer>
    </div>
    </div>
        CartList
        <CartItems/>
    </div>
    )
}
